import React, { useState } from 'react';
import FilterOverlay from './FilterOverlay.component';

const FilterOverlayContainer = (props) => {
    const [expand, setExpand] = useState(false);

    const handleExpandOptions = () => {
        setExpand(!expand);
    };

    const containerFunctions = {
        handleExpandOptions: () => handleExpandOptions()
    };

    const containerProps = () => {
        const { product: { searchData: { filters = [], total } } } = props;

        return {
            filters,
            total,
            expand
        };
    };

    return (
        <FilterOverlay 
            { ...containerProps() }
            { ...containerFunctions }
        />
    );
};

export default FilterOverlayContainer;
