export const searchReducer = (state, action) => {
    switch (action.type) {
        case 'SEARCH_DATA': {
            const { payload } = action;
            return {
                ...state,
                searchData: payload,
                isLoading: payload?.total
            };
        }

        default:
            break;
    }
};
