import React from 'react';
import Product from '../Product/Product.component';
import './SearchPage.style.scss';

const SearchPage = (props) => {
    const { onHandleChange, state } = props;
    console.log(state);

    const renderProductCard = () => {
        const { state: { searchData: { details }, isLoading } } = props;

        if(details.length === 0) {
            return Array.from(
                { length: 4 },
                (_, i) => {
                    return (
                        <Product
                            key={ i }
                            isLoading
                        />
                    );
                }
            );
        }

        const product =  details?.map((product) => {
            return (
                <Product key={product.__id} product={product} isLoading={ isLoading } />
            );
        });

        return product;
    }

    return (
        <>
            <div>Search Page</div>
            <div className='SearchField'>
                <input type='search' onChange={ (e) => onHandleChange(e) }/>
            </div>
            <div className='ProductBlocks'>
                <div className='Products'>
                    { renderProductCard() }
                </div>
            </div>
        </>
    );
};

export default SearchPage;
