import React, { useEffect, useState, useReducer, useCallback } from 'react';
import SearchPage from './SearchPage.component';
import { fetchSearchData } from '../../util/fetchApi';
import { debounce } from '../../util/Debounce';
import { searchReducer } from '../../store/search.reducer';

const SearchPageContainer = () => {
    const [searchText, updateSearch] = useState(null);
    const [state, dispatch] = useReducer(searchReducer, {
        searchData: []
    })

    const callApi = async() => {
        const data = await fetchSearchData(searchText);
        dispatch({
            type: 'SEARCH_DATA',
            payload: data
        })
    }

    useEffect(() => {
        callApi();
    }, [searchText]);

    const onHandleChange = useCallback((e) => {
        debounce(() => updateSearch(e.target.value), 500);
    });

    const containerFunctions = {
        onHandleChange: (e) => onHandleChange(e)
    };

    const containerProps = () => {
        return {
            state
        };
    };

    return (
        <SearchPage 
            { ...containerFunctions }
            { ...containerProps() }
        />
    );
};

export default SearchPageContainer;
