    const { product: { searchData: { total } }, setCurrentPage } = props;

    const renderPageSizes = () => {
        Array.from({ length: 5 }, (__, i) => i+1)
    }


    return (
        <div className='Pagination'>
            <button className='prevBtn'>Prev</button>
            { renderPageSizes() }
            <button className='nextBtn'>Next</button>
        </div>
    )
};

export default Pagination;
