import axios from "axios";
const qs = require('qs');

export const fetchSearchData = (searchText = 'men', pageNumber=1) => {
    const url = `https://api-r1.tagalys.com/v1/search?q=${searchText}&page=${pageNumber}`;



    const data = {
        'per_page': '16',
        'request[total]': 'total',
        'request[results]': 'results',
        'request[details]': 'details',
        'request[sort_options]': 'sort_options',
        'request[]': 'filters',
        'identification[client_code]': '92D5626DBFD74912',
        'identification[api_key]': '5dff8cbc1c833937d5b6ff490de6639b',
        'identification[store_id]': '77435339050'
    }

    let config = {
        method: 'post',
        maxBodyLength: Infinity,
        url: url,
        headers: { 
          'Content-Type': 'application/x-www-form-urlencoded'
        },
        data: data
      };


    const response = axios.request(config);
    console.log(response);

}

