import React from 'react';
import TextPlaceholder from '../TextPlaceholder';
// import { formatCurrency } from '../../util/Price';
import './FilterOverlay.style.scss';

const Product = (props) => {
    console.log(props);

    const renderfilterOption = (options) => {
        const option = options?.map((option) => {
            return (
                <div>
                    <input 
                        type='checkbox'
                        className=''
                        value={ option.name }
                        id={ option.id }
                    />
                    <label id={ option.id }>{option.name}</label>
                </div>
            );
        });

        return option;
    }

    const renderFilters = () => {
        const { filters, total } = props;

        const filter = filters?.map((filter) => {
            const { name, __id, items } = filter;
            return (
                <article key={__id} className='Filter'>
                    <div className='FilterName'>
                        { total ? 
                        (<>
                            <p>{name}</p>
                            <span>+</span>
                        </> ) :
                         <TextPlaceholder length='medium' /> }
                    </div>
                    <div className='FilterOptions'>
                        { renderfilterOption(items) }
                    </div>
                </article>
            )
        });

        return filter;
    }

    return (
        <>
            <div className='FilterOverlay'>
                <h4>Filters</h4>
                { renderFilters() }
            </div>
        </>
    );
};

export default Product;
