const API_BASE_URL = 'https://cors-anywhere.herokuapp.com/https://api-r1.tagalys.com/v1';

export const fetchData = async (query, page) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/search?q=tops&page=${page}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
};
