import React from 'react';
import TextPlaceholder from '../TextPlaceholder';
// import { formatCurrency } from '../../util/Price';
import './FilterOverlay.style.scss';
import FilterExpandable from '../FilterExpandable/FilterExpandable.component';

const Product = (props) => {
    console.log(props);

    const renderfilterOption = (options) => {
        const option = options?.map((option) => {
            return (
                <div className='FilterOption'>
                    <input 
                        type='checkbox'
                        value={ option.name }
                        id={ option.id }
                    />
                    <label id={ option.id }>{option.name}</label>
                </div>
            );
        });

        return option;
    }

    const renderFilters = () => {
        const { filters, total, handleExpandOptions, expand } = props;

        const filter = filters?.map((filter) => {
            const { name, __id, items } = filter;
            return (
                <FilterExpandable  name={ name } id={ __id } />
            )
        });

        return filter;
    }

    return (
        <>
            <div className='FilterOverlay'>
                <h4>Filters</h4>
                { renderFilters() }
            </div>
        </>
    );
};

export default Product;
