export const searchReducer = (state, action) => {
    switch (action.type) {
        case 'SEARCH_DATA': {
            const { payload } = action;
            return {
                ...state,
                searchData: action.payload,
                isLoading: false
            };
        }

        default:
            break;
    }
};
