export const Pagination = (props) => {
    const { dispatch, product: { currentPage, isLoading } } = props;

    const setCurrentPage = (value) => {
        console.log(value);
        dispatch({
            type: 'SET_CURRENT_PAGE',
            page: value
        });
    }

    const renderPageSizes = () => {
        if (isLoading) {
            return Array.from({ length: 5 }, (__, i) => {
                return (
                    <div className="paginationPlaceholder">
                        { i+1 }
                    </div>
                )
            });
        }

        return Array.from({ length: 5 }, (__, i) => {
            return (
                <button className="PaginationNumbers" onClick={ () => setCurrentPage(i+1) }>
                    { i+1 }
                </button>
            )
        });
    }


    return (
        <div className='Pagination'>
            <button
            className='prevBtn'
            role="button"
            disabled={ currentPage===1 }  onClick={ () => dispatch({ type: 'PREV_PAGE', page: currentPage - 1 }) }>Prev</button>
            { renderPageSizes() }
            <button className='nextBtn' disabled={ currentPage===5 }  onClick={ () => dispatch({ type: 'NEXT_PAGE', page: currentPage + 1 }) }>Next</button>
        </div>
    )
};

export default Pagination;
